# Operasi artimatika dipython
a = 12
b = 4

# Operasi pertambahan +
hasil = a + b
print(a, '+', b, '=', hasil)

# Operasi pengurangan -
hasil = a - b
print(a, '-', b, '=', hasil)

# Operasi perkalian *
hasil = a + b
print(a, '*', b, '=', hasil)

# Operasi pembagian /
hasil = a / b
print(a, '/', b, '=', hasil)

# Operasi pertambahan modulus %
hasil = a % b
print(a, '%', b, '=', hasil)

# Operasi eksponen (pangkat) **
hasil = a ** b
print(a, "**", b, '=', hasil)

# Operasi floor division //
hasil = a // b
print(a, '//', b, '=', hasil)

# operasi aritmatika prioritas
x = 10
y = 7
z = 12

hasil = x ** y * z + y / 4 + y - z % x
print(x,'**',y,'*',z,'+',y,'/',4,'+',y,'-',z,'%',x,'=', hasil)